const path = require\(''path''\);
const fs = require\(''fs''\);
const os = require\(''os''\);

const homeDir = os.homedir\(\);
const safariPath = path.join\(homeDir, ''Library/Safari/History.db''\);

console.log\(''Safari path:'', safariPath\);
console.log\(''Exists:'', fs.existsSync\(safariPath\)\);

if \(fs.existsSync\(safariPath\)\) {
  const stats = fs.statSync\(safariPath\);
  console.log\(''File size:'', stats.size, ''bytes''\);
  console.log\(''Readable:'', fs.accessSync\(safariPath, fs.constants.R_OK\) === undefined\);

  // Try to read first few bytes to check if it''s SQLite
  try {
    const fd = fs.openSync\(safariPath, ''r''\);
    const buffer = Buffer.alloc\(16\);
    fs.readSync\(fd, buffer, 0, 16, 0\);
    fs.closeSync\(fd\);
    const header = buffer.toString\(''utf8'', 0, 16\);
    console.log\(''SQLite header check:'', header.startsWith\(''SQLite format 3''\)\);
  } catch \(err\) {
    console.log\(''Read error:'', err.message\);
  }
}
